import "./drop-down-list.css";
import React, { useState } from 'react';
import styled from 'styled-components';



export const DropDownList = () => {
  const DropdownOption = styled.option`cursor: pointer;
  `;
  const [selectedOption, setSelectedOption] = useState('1');


  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const changeOptionStyle = (optionElement) => {
    optionElement.style.fontSize = '16px';
    optionElement.style.color = '#f00';
    optionElement.style.backgroundColor = '#fff';
  }


  return (
    <div dropdown>
      <select
        className="dropdown__select"
        id="dropdown"
        value={selectedOption}
        onChange={handleOptionChange}
      >
        <DropdownOption value="1" id="option1" ref={changeOptionStyle}>
          Вариант 1132
        </DropdownOption>
        <DropdownOption value="2" id="option2" ref={changeOptionStyle}>
          Вариант 22131
        </DropdownOption>
        <DropdownOption value="3" id="option3" ref={changeOptionStyle}>
          Вариант 31231
        </DropdownOption>
      </select>
    </div>
  );
};
