import "./drop-down-list.css";
import React, { useState } from 'react';

export const Lab3 = () => {
  const [selectedOption, setSelectedOption] = useState('1');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div dropdown>
      <select
        className="dropdown__select"
        id="dropdown"
        value={selectedOption}
        onChange={handleOptionChange}
      >
        <option className="dropdown__option" value="1" id="option1">
          Вариант 1132
        </option>
        <option className="dropdown__option" value="2" id="option2">
          Вариант 22131
        </option>
        <option className="dropdown__option" value="3" id="option3">
          Вариант 31231
        </option>
      </select>
    </div>
  );
}
