import "./drop-down-list.css";
import React, { useState } from 'react';
import styled from 'styled-components';

const DropdownOption = styled.option`cursor: pointer;`;

export const DropDownList = () => {
  const [selectedOption, setSelectedOption] = useState('1');


  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div dropdown>
      <select
        className="dropdown__select"
        id="dropdown"
        value={selectedOption}
        onChange={handleOptionChange}
      >
        <DropdownOption className="dropdown__option" value="1" id="option1">
          Вариант 1132
        </DropdownOption>
        <DropdownOption className="dropdown__option" value="2" id="option2">
          Вариант 22131
        </DropdownOption>
        <DropdownOption className="dropdown__option" value="3" id="option3">
          Вариант 31231
        </DropdownOption>
      </select>
    </div>
  );
};
